import axios from 'axios';

class AuthService {
  login(email, password) {
    return axios
      .post('http://localhost:5010/api/v1/auth/login', { email, password })
      .then((response) => {
        // If authentication succeeds, store the token in localStorage
        localStorage.setItem('token', response.data.token);
        return response.data;
      })
      .catch((error) => {
        throw error.response.data;
      });
  }

  register(firstName, lastName, email, password, nationality) {
    return axios
      .post('http://localhost:5010/api/v1/auth/register', { firstName, lastName, email, password, nationality })
      .then((response) => {
        // If registration succeeds, store the token in localStorage
        localStorage.setItem('token', response.data.token);
        return response.data;
      })
      .catch((error) => {
        throw error.response.data;
      });
  }

  logout() {
    // Clear the token from localStorage
    localStorage.removeItem('token');
  }

  isAuthenticated() {
    // Check if the token is present in localStorage
    return localStorage.getItem('token') !== null;
  }
}

export default new AuthService();
