import React, { useState } from 'react';
import { Link, Navigate } from 'react-router-dom';
import AuthService from './AuthService';
import './login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [redirectToReferrer, setRedirectToReferrer] = useState(false);
  const [id, setId] = useState(null); // add state for id parameter

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLoginClick = () => {
    AuthService.login(email, password).then((response) => {
      // if authentication is successful, set the id parameter in the state
      setId(response.id);
      setRedirectToReferrer(true);
    });
  };

  if (redirectToReferrer) {
    // pass the id parameter to MyPage component as a prop
    return <Navigate to={`/api/v1/destination/${id}`} />;
  }

  return (
    <form className="loginForm">
      <h2>Login</h2>
      <div>
        <label htmlFor="email">Email:</label>
        <input type="text" id="email" value={email} onChange={handleEmailChange} required />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input type="text" id="password" value={password} onChange={handlePasswordChange} required />
      </div>
      <button type="button" className="button" onClick={handleLoginClick}>
        Login
      </button>
      <div className="not-registered">
        Not registered? <Link to="/register">Register here</Link>
      </div>
    </form>
  );
};

export default Login;
