import React, { useEffect, useState } from 'react';
import { format } from 'date-fns';
import axios from 'axios';
import styles from './myFlights.css';
import {  useLocation } from 'react-router-dom';
function MyFlights() {
  const [bookings, setBookings] = useState([]);
  const [userId, setUserId] = useState(null);
  useEffect(() => {
    async function fetchData() {
      try {
        const parts = location.pathname.split('/');
        const userId = parts[4];
        setUserId(userId);
        const response = await axios.get(`http://localhost:5010/api/v1/myFlights/${userId}`);
        const { data } = response.data;
        setBookings(data);
      } catch (error) {
        console.error(error);
      }
    }
    fetchData();
  }, [location]);

  return (
    <div className={styles.myFlights}>
      <h2>Flight History</h2>
      <table>
        <thead>
          <tr>
            <th>Destination</th>
            <th>Price</th>
            <th>Flight Date</th>
            <th>Return Date</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking._id}>
              <td>{booking.destination.name}</td>
              <td>{booking.cost} {booking.destination.currency}</td>
              <td>{format(new Date(booking.flightDate), 'MM/dd/yyyy')}</td>
              <td>{format(new Date(booking.returnDate), 'MM/dd/yyyy')}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default MyFlights;
