import React, { useState } from 'react';
import Login from './Login';
import Register from './Register';
import './register.css';

const LoginRegisterForm = ({ onLogin, onRegister }) => {
  const [showLogin, setShowLogin] = useState(true);

  const handleLoginClick = (email, password) => {
    onLogin(email, password);
  };

  const handleRegisterClick = (firstName, lastName, email, password, nationality) => {
    onRegister(firstName, lastName, email, password, nationality);
  };

  const handleToggleClick = () => {
    setShowLogin(!showLogin);
    if (showLogin) {
      window.location.href = '/login';
    } else {
      window.location.href = '/register';
    }
  };

  return (
    <div className="form-wrapper">
      <div className="toggle-buttons">
        <button className={showLogin ? 'active' : ''} onClick={handleToggleClick}>
          Login
        </button>
        <button className={!showLogin ? 'active' : ''} onClick={handleToggleClick}>
          Register
        </button>
      </div>
      {showLogin ? <Login onLoginClick={handleLoginClick} /> : <Register onRegisterClick={handleRegisterClick} />}
    </div>
  );
};

export default LoginRegisterForm;
